import Layout from '../common/Layout';
import { useState, useRef } from 'react';

function Location() {
	const { kakao } = window;
	// 37.424500, 126.881859
	// 37.480850, 126.896526
	// 37.521632, 126.855402

	const path = process.env.PUBLIC_URL;
	const desc =
		'Lorem ipsum dolor sit amet consectetur adipisicing elit. Vel quam voluptate pariatur id mollitia, maxime nam aperiam enim. Ipsa quae commodi ipsum soluta cumque corporis minus ad, a itaque animi?';
	const location = [
		'Gwangmyeong, Gyeonggi, Korea',
		'Guro, Seoul, Korea',
		'Yangcheon, Seoul, Korea',
	];

	// Traffic
	const [Traffic, setTraffic] = useState(false);
	const [LocationIndex, setLocationIndex] = useState(0);
	const container = useRef(null);

	const mapOption = {
		center: new kakao.maps.LatLng(33.450701, 126.570667), // 지도의 중심좌표
		level: 3, // 지도의 확대 레벨
	};

	// 지도를 표시할 div와  지도 옵션으로  지도를 생성합니다
	// const map = new kakao.maps.Map(container.current, mapOption);

	return (
		<>
			<Layout
				name='Location'
				desc={desc}
				visual={`${path}/img/sub/location/visual.jpg`}></Layout>

			<div id='location'>
				<div className='locationBox'>
					<h3>Come see us</h3>
					<ul>
						<li className='on'>
							<button>IKEA</button>
						</li>
						<li>
							<button>LIVART</button>
						</li>
						<li>
							<button>CASAMIA</button>
						</li>
						<li>
							<button>{Traffic ? 'Traffic OFF' : 'Traffic ON'}</button>
						</li>
					</ul>
					<div id='map' ref={container}>
						container
					</div>
				</div>
				<div className='locationCaption'>
					<ul>
						<li>
							<h4>Address</h4>
							<p>{location[LocationIndex]}</p>
						</li>
						<li>
							<h4>Tel</h4>
							<p>+82 10-1234-5678</p>
						</li>
						<li>
							<h4>Email</h4>
							<p>xkdtndua15@gmail.com</p>
						</li>
					</ul>
				</div>
			</div>
		</>
	);
}

export default Location;
