import Layout from '../common/Layout';
import { useState, useRef, useEffect } from 'react';

function Community() {
	const desc =
		'Lorem ipsum dolor sit, amet consectetur adipisicing elit. Consequatur nulla eos quisquam mollitia voluptatibus quasi quaerat. Quasi a rerum inventore quo laudantium aspernatur nisi, assumenda dolorum nulla unde facilis reprehenderit.';
	const path = process.env.PUBLIC_URL;

	const [Posts, setPosts] = useState([{}]);
	const [EnableEdit, setEnableEdit] = useState(false);

	const input = useRef(null);
	const textarea = useRef(null);
	const inputEdit = useRef(null);
	const textareaEdit = useRef(null);

	const resetInput = () => {
		input.current.value = '';
		textarea.current.value = '';
	};
	const postInput = () => {
		setPosts([
			{ title: input.current.value, content: textarea.current.value },
			...Posts,
		]);
	};

	const editPost = () => {
		setEnableEdit(true);
	};

	return (
		<>
			<Layout
				name='Community'
				desc={desc}
				visual={`${path}/img/sub/community/visual.jpg`}></Layout>
			<div id='community'>
				<h3>Our Community</h3>
				<div className='inputBox'>
					<input type='text' placeholder='제목을 입력하세요' ref={input} />
					<br />
					<textarea
						cols='30'
						rows='5'
						placeholder='본문을 입력하세요'
						ref={textarea}></textarea>
					<br />

					<div className='btns'>
						<button onClick={() => resetInput()}>CANCEL</button>
						<button onClick={() => postInput()}>WRITE</button>
					</div>
				</div>
				<div className='showBox'>
					{Posts.map((post, idx) => {
						return (
							<article key={idx}>
								<div className='txt'>
									<h4>{post.title}</h4>
									<p>{post.content}</p>
								</div>
								<div className='btns'>
									<button>EDIT</button>
									<button>DELETE</button>
								</div>
							</article>
						);
					})}
				</div>
			</div>
		</>
	);
}

export default Community;
