import Layout from '../common/Layout';
import { useState } from 'react';

function Location() {
	// 37.424500, 126.881859
	// 37.480850, 126.896526
	// 37.521632, 126.855402

	const path = process.env.PUBLIC_URL;
	const desc =
		'Lorem ipsum dolor sit amet consectetur adipisicing elit. Vel quam voluptate pariatur id mollitia, maxime nam aperiam enim. Ipsa quae commodi ipsum soluta cumque corporis minus ad, a itaque animi?';
	const location = [
		'Gwangmyeong, Gyeonggi, Korea',
		'Guro, Seoul, Korea',
		'Yangcheon, Seoul, Korea',
	];

	// Traffic
	const [Traffic, setTraffic] = useState(false);
	const [LocationIndex, setLocationIndex] = useState(0);

	return (
		<>
			<Layout
				name='Location'
				desc={desc}
				visual={`${path}/img/sub/location/visual.jpg`}></Layout>

			<div id='location'>
				<div className='locationBox'>
					<h3>Come see us</h3>
					<ul>
						<li className='on'>IKEA</li>
						<li>LIVART</li>
						<li>CASAMIA</li>
						<li>{Traffic ? 'Traffic OFF' : 'Traffic ON'}</li>
					</ul>
					<div className='map'></div>
				</div>
				<div className='locationCaption'>
					<ul>
						<li>{location[LocationIndex]}</li>
						<li></li>
						<li></li>
					</ul>
				</div>
			</div>
		</>
	);
}

export default Location;
