import Layout from '../common/Layout';
import { useState, useRef, useEffect } from 'react';

function Location() {
	const { kakao } = window;
	// 37.424500, 126.881859
	// 37.480850, 126.896526
	// 37.521632, 126.855402
	const info = [
		{
			title: 'IKEA',
			location: 'Gwangmyeong, Gyeonggi, Korea',
			latlng: new kakao.maps.LatLng(37.4245, 126.881859),
		},
		{
			title: 'LIVART',
			location: 'Guro, Seoul, Korea',
			latlng: new kakao.maps.LatLng(37.480851, 126.896526),
		},
		{
			title: 'CASAMIA',
			location: 'Yangcheon, Seoul, Korea',
			latlng: new kakao.maps.LatLng(37.521632, 126.855402),
		},
	];

	const path = process.env.PUBLIC_URL;
	const desc =
		'Lorem ipsum dolor sit amet consectetur adipisicing elit. Vel quam voluptate pariatur id mollitia, maxime nam aperiam enim. Ipsa quae commodi ipsum soluta cumque corporis minus ad, a itaque animi?';

	// Traffic
	const [Traffic, setTraffic] = useState(false);
	const [LocationIndex, setLocationIndex] = useState(0);
	const container = useRef(null);

	useEffect(() => {
		const mapOption = {
			center: new kakao.maps.LatLng(33.450701, 126.570667),
			level: 3,
		};

		const mapInst = new kakao.maps.Map(container.current, mapOption);
	}, []);

	return (
		<>
			<Layout
				name='Location'
				desc={desc}
				visual={`${path}/img/sub/location/visual.jpg`}></Layout>

			<div id='location'>
				<div className='locationBox'>
					<h3>Come see us</h3>
					<ul>
						<li className='on'>
							<button>IKEA</button>
						</li>
						<li>
							<button>LIVART</button>
						</li>
						<li>
							<button>CASAMIA</button>
						</li>
						<li>
							<button onClick={() => console.log(container.current)}>
								{Traffic ? 'Traffic OFF' : 'Traffic ON'}
							</button>
						</li>
					</ul>
					<div id='map' ref={container}></div>
				</div>
				<div className='locationCaption'>
					<ul>
						<li>
							<h4>Address</h4>
							<p>{location[LocationIndex]}</p>
						</li>
						<li>
							<h4>Tel</h4>
							<p>+82 10-1234-5678</p>
						</li>
						<li>
							<h4>Email</h4>
							<p>xkdtndua15@gmail.com</p>
						</li>
					</ul>
				</div>
			</div>
		</>
	);
}

export default Location;
